import React, { useState } from "react";
import "./index.css";

export default function BurnoutSurvey() {
  const [scoreSummary, setScoreSummary] = useState(null);

  const labels = [
    "Ï†ÑÌòÄÏóÜÎã§",
    "1ÎÖÑÏóê 2-3Ìöå ÎòêÎäî Í∑∏ ÎØ∏Îßå",
    "Ìïú Îã¨Ïóê Ìïú Î≤à ÎòêÎäî Í∑∏ ÎØ∏Îßå",
    "Ìïú Îã¨Ïóê 2-3Ìöå",
    "ÏùºÏ£ºÏùºÏóê 1Ìöå Ï†ïÎèÑ",
    "ÏùºÏ£ºÏùºÏóê 2-3Ìöå",
    "Îß§Ïùº"
  ];

  const questions = [
    "Îß°ÏùÄ ÏùºÏùÑ ÌïòÎäî Îç∞ ÏûàÏñ¥ÏÑú Ï†ïÏÑúÏ†ÅÏúºÎ°ú Í≥†Í∞àÎêú ÎäêÎÇåÏù¥ Îì†Îã§.",
    "ÏßÅÏû• ÏùºÏùÑ ÎßàÏπòÍ≥† Ìá¥Í∑ºÌï† ÎïåÏØ§Ïù¥Î©¥ Í∏∞ÏßÑÎß•ÏßÑÌïú ÎäêÎÇåÏù¥ Îì†Îã§.",
    "ÏïÑÏπ®Ïóê ÏùºÏñ¥ÎÇòÏÑú Îã§Ïãú Ï∂úÍ∑ºÌï† ÏÉùÍ∞ÅÏùÑ ÌïòÎ©¥ ÌîºÍ≥§Ìïú ÎäêÎÇåÏù¥ Îì†Îã§.",
    "ÌïôÏÉùÎì§Ïùò Í∞êÏ†ïÏùÑ ÏâΩÍ≤å Ïù¥Ìï¥Ìï† Ïàò ÏûàÎã§.",
    "ÏùºÎ∂Ä ÌïôÏÉùÎì§Ïóê ÎåÄÌï¥ÏÑú Ïù∏Í≤©Ïù¥ ÏóÜÎäî Î¨ºÏ≤¥Ï≤òÎüº ÎåÄÌïòÍ≥† ÏûàÎã§Í≥† ÎäêÎÇÄÎã§.",
    "ÌïòÎ£® Ï¢ÖÏùº ÏÇ¨ÎûåÎì§Í≥º Ìï®Íªò ÏùºÌïúÎã§Îäî Í≤ÉÏùÄ ÌûòÎì† ÏùºÏù¥Îã§.",
    "ÌïôÏÉùÎì§Ïùò Î¨∏Ï†úÎ•º Ìö®Í≥ºÏ†ÅÏúºÎ°ú Ìï¥Í≤∞ÌïúÎã§.",
    "Ïùº ÎïåÎ¨∏Ïóê ÏÜåÏßÑÎêú ÏÉÅÌÉúÏù¥Îã§.",
    "ÏùºÏùÑ ÌÜµÌï¥ Îã§Î•∏ ÏÇ¨ÎûåÎì§Ïùò ÏÇ∂Ïóê Í∏çÏ†ïÏ†ÅÏù∏ ÏòÅÌñ•ÏùÑ Ï£ºÍ≥† ÏûàÎã§Í≥† ÎäêÎÇÄÎã§",
    "ÏÑ†ÏÉùÎãòÏù¥ Îêú Ïù¥ÌõÑÎ°ú ÏÇ¨ÎûåÎì§ÏóêÍ≤å Ï†êÏ†ê ÎëîÍ∞êÌï¥Ï°åÎã§.",
    "Ïù¥ ÏßÅÏóÖÏúºÎ°ú Ïù∏Ìï¥ ÎÇ¥Í∞Ä Ï†ïÏÑúÏ†ÅÏúºÎ°ú Î©îÎßêÎùºÍ∞ÄÎäî Í≤É Í∞ôÏïÑ Í±±Ï†ïÏù¥Îã§.",
    "Îß§Ïö∞ ÌôúÍ∏∞Ï∞¨ ÎäêÎÇåÏù¥ Îì†Îã§.",
    "ÏóÖÎ¨¥Î°ú Ïù∏Ìï¥ Ï¢åÏ†àÍ∞êÏùÑ ÎäêÎÇÄÎã§.",
    "Ï£ºÏñ¥ÏßÑ ÏùºÏùÑ ÏßÄÎÇòÏπòÍ≤å Ïó¥Ïã¨Ìûà ÌïúÎã§Í≥† ÎäêÎÇÄÎã§.",
    "Ïñ¥Îñ§ ÌïôÏÉùÎì§ÏóêÍ≤åÎäî Î¨¥Ïä® ÏùºÏù¥ ÏùºÏñ¥ÎÇ¨ÎäîÏßÄ Î≥ÑÎ°ú Ïã†Í≤Ω Ïì∞ÏßÄ ÏïäÎäîÎã§.",
    "ÏÇ¨ÎûåÎì§Í≥º ÏßÅÏ†ë ÎåÄÌïòÎ©¥ÏÑú ÏùºÌïòÎäî Í≤ÉÏóê Ïä§Ìä∏Î†àÏä§Î•º Î∞õÎäîÎã§.",
    "ÌïôÏÉùÎì§Í≥º ÏâΩÍ≤å Ìé∏ÏïàÌïú Î∂ÑÏúÑÍ∏∞Î•º ÎßåÎì§ Ïàò ÏûàÎã§.",
    "ÌïôÏÉùÎì§Í≥º ÏπúÎ∞ÄÌïòÍ≤å ÏùºÌïòÍ≥† ÎÇòÎ©¥ ÌùêÎ≠áÌï¥ÏßÑÎã§.", 
    "ÏßÅÏóÖÏùÑ ÌÜµÌï¥ Í∞ÄÏπò ÏûàÎäî ÎßéÏùÄ Í≤ÉÎì§ÏùÑ ÏÑ±Ï∑®Ìï¥ÏôîÎã§.",
    "ÌïúÍ≥ÑÏóê Îã§Îã§Î•∏ ÎäêÎÇåÏù¥ Îì†Îã§.", 
    "ÏßÅÏû•ÏóêÏÑú Í∞êÏ†ïÏ†ÅÏù∏ Î¨∏Ï†úÎì§ÏùÑ Îß§Ïö∞ Ïπ®Ï∞©ÌïòÍ≤å Îã§Î£¨Îã§.", 
    "ÌïôÏÉùÎì§Ïù¥ Ïñ¥Îñ® ÎïåÎäî ÏûêÏã†Îì§Ïùò Î¨∏Ï†úÎì§Ïóê ÎåÄÌï¥ ÎÇòÎ•º ÎπÑÎÇúÌïòÍ≥† ÏûàÎã§Í≥† ÎäêÎÇÄÎã§."
  ];

  const handleSubmit = () => {
    const getValue = (i) => {
      const selected = document.querySelector(`input[name='q${i}']:checked`);
      return selected ? parseInt(selected.value, 10) : 0;
    };

    const exhaustionIdx = [0, 1, 2, 5, 7, 12, 13, 15, 19];
    const depersonalIdx = [4, 9, 10, 14, 21];
    const efficacyIdx = [3, 6, 8, 11, 16, 17, 18, 20];

    const exhaustion = exhaustionIdx.reduce((sum, i) => sum + getValue(i), 0) / exhaustionIdx.length;
    const depersonal = depersonalIdx.reduce((sum, i) => sum + getValue(i), 0) / depersonalIdx.length;
    const efficacy = efficacyIdx.reduce((sum, i) => sum + getValue(i), 0) / efficacyIdx.length;

    const exhaustionT = 50 + 10 * (exhaustion - 21.25) / 11.01;
    const depersonalT = 50 + 10 * (depersonal - 11.0) / 6.19;
    const efficacyT = 50 + 10 * (efficacy - 33.54) / 6.89;

    setScoreSummary({ exhaustion, depersonal, efficacy, exhaustionT, depersonalT, efficacyT });
  };

  const renderResult = () => {
    if (!scoreSummary) return null;
    const { exhaustion, depersonal, efficacy, exhaustionT, depersonalT, efficacyT } = scoreSummary;
    return (
      <div className="mt-6 text-center bg-indigo-50 border border-indigo-200 p-4 rounded-md">
        <p className="font-semibold">ÏÜåÏßÑ ÌèâÍ∑†: {exhaustion.toFixed(2)} / T Ï†êÏàò: {exhaustionT.toFixed(1)}</p>
        <p className="font-semibold">ÎπÑÏù∏Í≤©Ìôî ÌèâÍ∑†: {depersonal.toFixed(2)} / T Ï†êÏàò: {depersonalT.toFixed(1)}</p>
        <p className="font-semibold">Ìö®Îä•Í∞ê ÌèâÍ∑†: {efficacy.toFixed(2)} / T Ï†êÏàò: {efficacyT.toFixed(1)}</p>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-white py-6 font-sans text-gray-800">
      <div className="max-w-3xl mx-auto px-4">
        <main className="space-y-6">
          <section className="bg-gray-100 rounded-xl shadow p-4">
